import Link from 'next/link'
import Head from 'next/head'

import React, { Component } from 'react'

class Layout extends Component {


    render () {
        const { children, title } = this.props
        return (

            <div>
                <Head>
                    <title>{title}</title>
                </Head>
                <header>
                    <Link href="/">
                        <a >
                            Podcast
                        </a>
                    </Link>
                </header>

                { children }

                <style jsx>{`
                header{
                        color: #fff;
                        background: #8756ca;
                        padding: 15px;
                        text-align: center;
                    }
                
                header a {
                    color: #ffff;
                    text-decoration: none;
                }
                `}</style>

                

            </div>
        )
    }
}

export default Layout
