import Link from 'next/link'

import React, { Component } from 'react'

class Layout extends Component {
    render () {
        return (
            <div>
                <header>
                    <Link href="/">
                        <a href="">
                            Podcast
                        </a>
                    </Link>
                </header>

                <style jsx>{`
                header{
                        background: #8756ca;
                        padding: 15px;
                        text-align: center;
                    }
                `}</style>

                <style jsx global>{`
                    body{
                        margin: 0;
                        padding: 0;
                        background: white;
                        font-family: system-ui;
                    }
                    
                    a{
                        color: fff;
                        text-decoration: none;
                    }
                `}</style>

            </div>
        )
    }
}

export default Layout
