import Link from 'next/link'

export default class extends React.Component {
    static async getInitialProps({query}){
        const id = query.id
        const requestAudio =  await fetch(`https://api.audioboom.com/audio_clips/${id}.mp3`)
        const response = await requestAudio.json()
        const dataAudio = response.body.audio_clip
        return { dataAudio }
    }
    render () {
        const { dataAudio } = this.props
        console.log(dataAudio)
        return (
            <div>
                <header>Podcast</header>

                <div className="modal">
                    <div className="clip">
                        <nav>
                            <Link href={`/channel?id=${dataAudio.channel.id}`}>
                                <a className="close">&lt; Volver</a>
                            </Link>
                        </nav>

                        <picture>
                            <div 
                            style={{backgroundImage: 
                            `url(${dataAudio.urls.image || dataAudio.channel.urls.logo_image.original})`
                            }} 
                            />
                        </picture>

                        <div className="player">
                            <h3> {dataAudio.title} </h3>
                            <h6> {dataAudio.channel.title} </h6>
                            <audio constrols autoPlay={true} >
                                <source src={dataAudio.urls.high_mp3} type='audio/mpeg'/>
                            </audio>
                        </div>

                    </div>
                </div>

            </div>
        )
    }
}
