import React from 'react'

export default class extends React.Component {

    static async getInitialProps({ query }) {
        const idChannel = query.id

        let [requestChannel, requestAudio, requestSeries] = await Promise.all([
            fetch(`https://api.audioboom.com/channels/${idChannel}`),
            fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`),
            fetch(`https://api.audioboom.com/channels/${idChannel}/child_channels`)
        ])

        const dataChannel = await requestChannel.json()
        const channel = dataChannel.body.channel

        const dataAudio = await requestAudio.json()
        const audios = dataAudio.body.audio_clips

        const dataSeries = await requestSeries.json()
        const series = dataSeries.body.channels

        return { channel, audios, series }
    }

    render() {
        const { channel, audios, series } = this.props

        return (
            <div>
                <header>Podcast</header>

                <div className="banner">
                    <h1>{channel.title}</h1>
                </div>

                <style jsx >{`
                    header{
                        color: #fff;
                        background: #8756ca;
                        padding: 15px;
                        text-align: center;
                    }

                    .banner {
                        width: 100%;
                        padding-bottom: 25%;
                        background-position: 50% 50%;
                        background-size: cover;
                        background-color: #aaa;
                      }

                    .channels {
                        display: grid;
                        grid-gap: 15px;
                        padding: 15px;
                        grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
                    }

                    a.channel {
                        display: block;
                        margin-bottom: 0.5em;
                        color: #333;
                        text-decoration: none;
                    }

                    .channel img{
                        border-radius: 3px;
                        box-shadow: 0px 2px 6px rgba(0,0,0,0.15);
                        width: 100%;
                    }

                    h1 {
                        font-weight: 600;
                        padding: 15px;
                      }

                    h2 {
                        padding: 5px;
                        font-size: 0.9em;
                        font-weight: 600;
                        maring: 0;
                        text-align: center;
                    }
                `}</style>

                <style jsx global>{`
                    body{
                        margin: 0;
                        padding: 0;
                        background: white;
                        font-family: system-ui;
                    }
                `}</style>

            </div>
        )
    }
}
