import Link from 'next/link'

import React, { Component } from 'react'

class Layout extends Component {


    render () {
        const { children } = this.props
        return (
            <div>
                <header>
                    <Link href="/">
                        <a >
                            Podcast
                        </a>
                    </Link>
                </header>

                { children }

                <style jsx>{`
                header{
                        color: #fff;
                        background: #8756ca;
                        padding: 15px;
                        text-align: center;
                    }
                
                header a {
                    color: #ffff;
                    text-decoration: none;
                }
                `}</style>

                

            </div>
        )
    }
}

export default Layout
