import React from 'react'

export default class extends React.Component{

    static async getInitialProps({query}){
        const idChannel = query.id
        
       let [requestChannel, requestAudio, requestSeries] = await Promise.all([
           fetch(`https://api.audioboom.com/channels/${idChannel}`),
           fetch(`https://api.audioboom.com/channels/${idChannel}`),
           fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`)
       ])

       const dataChannel = await requestChannel.json()
       const channel = await dataChannel.body.channel

       const dataAudio = await requestAudio.json()
       const audio = await dataAudio.body.audio_clips

       const dataSeries = await requestSeries.json()
       const series = await dataSeries.body.channel

       return {channel, audio, series}
    }

    render(){
        const {channel, audio, series} = this.props
        console.log(channel)
        console.log(audio)
        console.log(series)

        return(
            <div>
                channel :D
            </div>
        )
    }
}
