import React from 'react'

export default class extends React.Component{

    static async getInitialProps({query}){
        const idChannel = query.id
        
       let [requestChannel, requestAudio, requestSeries] = await Promise.all([
           fetch(`https://api.audioboom.com/channels/${idChannel}`),
           fetch(`https://api.audioboom.com/channels/${idChannel}`),
           fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`)
       ])

       const dataChannel = requestChannel.json()
       const channel =  dataChannel.body.channel

       const dataAudio = requestAudio.json()
       const audio =  dataAudio.body.audio_clips

       const dataSeries = requestSeries.json()
       const series =  dataSeries.body.channel

       return {channel, audio, series}
    }

    render(){
        const {channel, audio, series} = this.props
        console.log(channel, audio, series)
        return(
            <div>
                channel :D
            </div>
        )
    }
}
