import React from 'react'

export default class extends React.Component {
    // {series.map((serie) => {
    //     return <div> {serie.title} </div>
    // })}

    // <h2>ultimos podcast</h2>
    // {audios.map((audio) => {
    //     return <div> {audio.title} </div>
    // })}
    static async getInitialProps({ query }) {
        const idChannel = query.id

        const requestChannel = await fetch(`https://api.audioboom.com/channels/${idChannel}`)
        const dataChannel = await requestChannel.json()
        const channel = dataChannel.body.channel

        const requestAudio = await fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`)
        const dataAudio = await requestAudio.json()
        const audios = dataAudio.body.audio_clips

        return { channel, audios }
    }
    render() {
        const { channel, audios } = this.props
        console.log(channel)
        console.log(audios)

        return (
            <div>
                <header>Podcast</header>
                <h1>{channel.title}</h1>
                <h2>Series</h2>
                

            </div>
        )
    }
}
